import React from 'react';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Image from 'react-bootstrap/Image';
import Container from 'react-bootstrap/Container';

export default class SummonerBrief extends React.Component{
    constructor(props)
    {
        super(props);
        this.state = {
            account:    {    
                            "id": "d7fEAV_7PTo3DJ2kMNlPg17Z73GydMHJdpFdaxlhxK-pMFE",
                            "accountId": "DtHPPWCQI1Watb-87E2AH3ftvRoGzu-KDAXYnlZCXVunsfo",
                            "puuid": "HR8FBtREaZ9TacLlQxFPPubNrlCdfQgUGPsKruL--3WZGcxc8-mNoDKwUoEcFx5f3pugrFtu5WACtw",
                            "name": "Maxnonimous",
                            "profileIconId": 1391,
                            "revisionDate": 1637606971744,
                            "summonerLevel": 203
                        },
            league:     {
                            "leagueId": "b68f3ec5-3aec-454b-b368-80f8fbd2f57c",
                            "queueType": "RANKED_SOLO_5x5",
                            "tier": "PLATINUM",
                            "rank": "IV",
                            "summonerId": "d7fEAV_7PTo3DJ2kMNlPg17Z73GydMHJdpFdaxlhxK-pMFE",
                            "summonerName": "Maxnonimous",
                            "leaguePoints": 40,
                            "wins": 121,
                            "losses": 111,
                            "veteran": false,
                            "inactive": false,
                            "freshBlood": false,
                            "hotStreak": false
                        },
            match:      [
                        "EUW1_5569915320",
                        "EUW1_5569809811",
                        "EUW1_5564404629",
                        "EUW1_5562882303",
                        "EUW1_5562635579",
                        "EUW1_5562558987",
                        "EUW1_5549608221",
                        "EUW1_5549585293",
                        "EUW1_5548528813",
                        "EUW1_5544080959",
                        "EUW1_5543974709",
                        "EUW1_5543246834",
                        "EUW1_5542538902",
                        "EUW1_5541281442",
                        "EUW1_5541130985",
                        "EUW1_5541081450",
                        "EUW1_5531587467",
                        "EUW1_5531611758",
                        "EUW1_5531498656",
                        ]    
        }
    }

    //getAccount();
    //getLeague(league);
    //getMatches(puuid);


    render(){
        const summonerName = this.state.account["name"];
        const profileIcon = `http://ddragon.leagueoflegends.com/cdn/11.23.1/img/profileicon/${this.state.account["profileIconId"]}.png`
        return(
            <div className="summonerBrief">
                <Container>
                <Row>
                    <Col>
            <Container className="sumBriefContainer">
                <Row>
                    <Col xs={2}><Image className="sumBriefImg" src={profileIcon} roundedCircle/></Col>
                    <Col><p><h3>{ summonerName }</h3>
                            Level {this.state.account["summonerLevel"]}</p></Col>
                </Row>
            </Container>
            </Col>
            <Col>
            <Container className="sumBriefContainer">
                <h3>League info</h3>
            </Container>
            </Col>
            </Row>
            </Container>
            </div>
        );
    }
}
