import React from 'react';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Image from 'react-bootstrap/Image';

import { useNavigate } from 'react-router-dom';
import { RiotAPI } from './RiotAPI';


export class Home extends React.Component{

    constructor(props)
    {
        super(props);
        this.state = {
            puuid: ""
        }
    }

    handleSubmit( event )
    {   

        event.preventDefault()
        let summonerName = event.target.elements.summonerName.value;

        const api = new RiotAPI();

        api.fetchSummonerByName(summonerName)
        .then((response) => {
            console.log (response.data);
            this.setState({
                puuid: response.data.puuid
            })

          })
         .catch((error)=>{
            console.log(error);
         });

         console.log(this.state);
    }
    render()
    {
        return(
            <div className="px-3 py-3 pt-md-5 pb-md-4 mx-auto text-center">
            <Image className="homeImg"src="./assets/logo_big.png"/><br/>
            <p className="lead">ECE's finest players</p>
            <Form className="homeForm" onSubmit={this.handleSubmit}> 
            <Row>
                <Col>
                <Form.Label htmlFor="inlineFormInput" visuallyHidden>
                    Summoner Name
                </Form.Label>
                <Form.Control
                    className="mb-2 sumControl"
                    name="summonerName"
                    placeholder="Summoner Name..."
                />
                </Col>
                <Col xs={2}>
                <Button type="submit" className="homeSubmitBtn mb-2 px-4">
                    &gt;
                </Button>
                </Col>
            </Row>
        </Form>
        </div>
        );
    }
}
