import axios from 'axios';

const API_KEY = "RGAPI-ef402363-973c-47a3-b9e9-8da620512265";
const API_URL = "https://euw1.api.riotgames.com";

/* ROUTES */
const API_SUMMONER = "lol/summoner/v4/summoners";
const API_MATCH = "lol/match/v5/matches";
const API_LEAGUE = "lol/league/v4/leagues";

export class RiotAPI {

    fetchSummonerByName(name){
        return axios
        .get(`${API_URL}/${API_SUMMONER}/by-name/${name}?${API_KEY}`, {
        crossdomain: true
        })
    }
}
