import React from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Container from 'react-bootstrap/Container';

import { ItemSet } from './ItemSet';
import { RiotAPI } from '../RiotAPI.js';


//URLS
const CHAMP_SQUARE_ASSET_URL = "http://ddragon.leagueoflegends.com/cdn/11.23.1/img/champion";
const SUMM_SPELL_ASSET_URL = "http://ddragon.leagueoflegends.com/cdn/11.23.1/img/spell";


export default class SummonerHistory extends React.Component{
    constructor(props)
    {
        super(props);
        this.state = {
            puiid: "",
            match:  []   
        }
    }

       
    getMatchesHistory()
    {
        const api = new RiotAPI();
        api.fetchMatchesHistory(this.puiid)
        .then(function(response)
        {
            console.log(response.data);
        })
        .catch(function (error) {
            // handle error
            console.log(error);
        });
    }
    getMatchDetail()
    {
        //return an array of detailed match history
        return {
            "assists": 6,
            "baronKills": 1,
            "bountyLevel": 0,
            "champLevel": 16,
            "championId": 19,
            "championName": "Warwick",
            "deaths": 14,
            "detectorWardsPlaced": 3,
            "individualPosition": "JUNGLE",
            "item0": 3047,
            "item1": 2031,
            "item2": 1011,
            "item3": 3748,
            "item4": 3076,
            "item5": 6632,
            "item6": 3340,
            "kills": 3,
            "lane": "JUNGLE",
            "profileIcon": 1391,
            "puuid": "HR8FBtREaZ9TacLlQxFPPubNrlCdfQgUGPsKruL--3WZGcxc8-mNoDKwUoEcFx5f3pugrFtu5WACtw",
            "summoner1Casts": 17,
            "summoner1Id": 11,
            "summoner2Casts": 5,
            "summoner2Id": 4,
            "summonerId": "d7fEAV_7PTo3DJ2kMNlPg17Z73GydMHJdpFdaxlhxK-pMFE",
            "totalMinionsKilled": 265,
            "summonerName": "Maxnonimous",
            "teamPosition": "JUNGLE",
            "visionScore": 34,
            "win": false
        };
    }

    getSummonerSpell( summonerSpellID )
    {
        let id = "";
        switch ( summonerSpellID ){
            case 4: id = "SummonerFlash.png"; break;
            case 11: id = "SummonerSmite.png"; break;
            case 14: id = "SummonerDot.png"; break;
        }
        return id;
    }


    render(){
        const history = this.getMatchDetail();
        console.log(history);
        const champIcon = `${CHAMP_SQUARE_ASSET_URL}/${history["championName"]}.png`;
        const summonerSpell1 = `${SUMM_SPELL_ASSET_URL}/${this.getSummonerSpell(history["summoner1Id"])}`;
        const summonerSpell2 = `${SUMM_SPELL_ASSET_URL}/${this.getSummonerSpell(history["summoner2Id"])}`;
        const KDA = `${history["kills"]}/${history["deaths"]}/${history["assists"]}`;
        const minionsKilled = history["totalMinionsKilled"];
        const KP = "kp%";
        console.log(champIcon);

        return(
            <div className="historyContainer victory">
                <Row>
                    <Col xs={2}>
                        <Row>
                            <Image className="historyChampIcon" src={champIcon}/> 
                        </Row>
                        <Container className="d-flex p-2" >
                            <Image className="historySummonerSpell" src={summonerSpell1}/>
                            <Image className="historySummonerSpell mx-1" src={summonerSpell2}/>
                        </Container>
                    </Col>

                    <Col>
                        <Container className="d-flex flex-column">
                            <h3>{KDA}</h3>
                            <h5>{minionsKilled} CS {KP}</h5>
                        </Container>
                    </Col>

                    <Col>
                        <ItemSet item0={history["item0"]} item1={history["item1"]} item2={history["item2"]} item3={history["item3"]} item4={history["item4"]} item5={history["item5"]}/>
                    </Col>
                </Row>
            </div>
        );
    }
}
