import axios from 'axios';

const API_KEY = "RGAPI-ef402363-973c-47a3-b9e9-8da620512265";
const API_URL = "https://euw1.api.riotgames.com";

/* ROUTES */
const API_SUMMONER = "lol/summoner/v4/summoners";
const API_MATCH = "lol/match/v5/matches";
const API_LEAGUE = "lol/league/v4/leagues";

const config = {
    headers:{
        "Access-Control-Allow-Origin" : "*",
        "Access-Control-Allow-Headers": "Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With",
        'Content-Type' : 'application/x-www-form-urlencoded; charset=UTF-8',

            }
  };
export class RiotAPI {

    fetchSummonerByName(name){

        const URL = `${API_URL}/${API_SUMMONER}/by-name/${name}?${API_KEY}`;
        return axios
        .get(URL, config)
    }
}
