import React from 'react';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Image from 'react-bootstrap/Image';

import { useNavigate } from 'react-router-dom';
import { RiotAPI } from './RiotAPI';


function navToSummonerPage(puuid)
{
    let navigate = useNavigate();
    return (
        <div>
            {navigate(`summoner`)}
        </div>
    )
}
export class Home extends React.Component{

    constructor(props)
    {
        super(props);
        this.state = {
            puuid: "",
        };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit( event )
    {   

        event.preventDefault()
        let summonerName = event.target.elements.summonerName.value;
        if (summonerName != "")
        {
            const api = new RiotAPI();
            api.fetchSummonerByName(summonerName)
            .then((response) => {
                this.setState({
                    puuid: response.data.puuid
                })
            })
            .catch((error)=>{
                console.log(error);
            });
        }

        
    }

    render()
    {
        let nav = null;
        if (this.state.puuid != "")
        {

        }
        return(
            <div className="px-3 py-3 pt-md-5 pb-md-4 mx-auto text-center">
            <Image className="homeImg"src="./assets/logo_big.png"/><br/>
            <p className="lead">ECE's finest players</p>
            <Form className="homeForm" onSubmit={this.handleSubmit}> 
            <Row>
                <Col>
                <Form.Label htmlFor="inlineFormInput" visuallyHidden>
                    Summoner Name
                </Form.Label>
                <Form.Control
                    className="mb-2 sumControl"
                    name="summonerName"
                    placeholder="Summoner Name..."
                />
                </Col>
                <Col xs={2}>
                <Button type="submit" className="homeSubmitBtn mb-2 px-4">
                    &gt;
                </Button>
                </Col>
            </Row>
        </Form>
        </div>
        );
    }
}
