import React from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Container from 'react-bootstrap/Container';

import RiotAPI  from '../tools/RiotAPI';

export default class SummonerBrief extends React.Component{
    constructor(props)
    {
        super(props);
        this.state = {
            name: props.summonerName,
            account:    {
                accountId: "",
                id: "",
                name: "",
                profileIconId: 0,
                puuid: "",
                revisionDate: 0,
                summonerLevel: 0,
            },
            league:     [],    
        }
        this.setAccount();
    }

    setAccount(){
        let api = new RiotAPI();
        api.fetchSummonerByName(this.state.name)
        .then((response) => {
            this.setState({
                account: response.data
            });
            console.log(this.state);
            this.setLeague();
        })
        .catch((error) => {
            console.log(error);
        });
    }
    setLeague()
    {
        let api = new RiotAPI();
        console.log(this.state.account.it);
