import React from 'react';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Image from 'react-bootstrap/Image';

import { RiotAPI } from './RiotAPI';
import { useNavigate } from 'react-router-dom';



export class Home extends React.Component{
    
    async handleSubmit( event )
    {
        event.preventDefault();
        let navigate = useNavigate(); 
        let result = await submitForm(event.target);
        if (result.error) {
        setError(result.error);
        } else {
        navigate('success');
        }
    }

    summonerForm()
    {
        return (
            <Form className="homeForm"> 
                <Row onSubmit={this.handleSubmit}>
                    <Col>
                    <Form.Label htmlFor="inlineFormInput" visuallyHidden>
                        Summoner Name
                    </Form.Label>
                    <Form.Control
                        className="mb-2 sumControl"
                        name="summonerName"
                        placeholder="Summoner Name..."
                    />
                    </Col>
                    <Col xs={2}>
                    <Button type="submit" className="homeSubmitBtn mb-2 px-4">
                        &gt;
                    </Button>
                    </Col>
                </Row>
            </Form>
        );
    }

    render()
    {
        
        return(
        <div className="px-3 py-3 pt-md-5 pb-md-4 mx-auto text-center">
            <Image className="homeImg"src="./assets/logo_big.png"/><br/>
             <p className="lead">ECE's finest players</p>
             {this.summonerForm()}
            
        </div>
        );
    }
}
